mapper.select:
SELECT H.BO_NO, H.SUBJECT, H.HM_TITLE, H.DEADLINE, HS.STATUS, HS.SCORE
FROM HOMEWORK H
LEFT JOIN HM_SUBMIT HS
ON H.BO_NO = HS.BO_NO
AND HS.STU_ID = #{stuId}
WHERE H.CLASS_CODE = #{classCode}

SELECT COUNT(*)
FROM HOMEWORK
WHERE CLASS_CODE = #{classCode}

		SELECT * 
    	  FROM HM_SUBMIT 
    	 WHERE BO_NO = #{boNo} 
      	   AND STU_ID = #{stuId}

mapper.update:
UPDATE HM_SUBMIT
   SET HM_STUCONTENT = #{hmStuContent}
 WHERE BO_NO = #{boNo}
